.social-links {
  display: flex;
  justify-content: end;
  align-items: center;
}
.social-links i {
  color: white;
  font-size: 20px;
  padding-left: 20px;
}

.page-footer {
  background-image: linear-gradient(40deg, #fbc2eb 40%, #a6c1ee 100%);
  background-size: 400%;
  animation: footer-animation 5s infinite alternate;
}

.page-footer:hover {
  background-image: linear-gradient(40deg, #fbc2eb 40%, #a6c1ee 100%);
}

@keyframes footer-animation {
  0% {
    background-position: left;
  }
  100% {
    background-position: right;
  }
}
